mapper.select:
select
cou.courses_id,courses_name,courses_pricemoney,courses_img_url
from courses_tab cou left join video_form_tab form on cou.video_form_id =
form.video_form_id
where form.video_form_class=#{video_form_class} and cou.courses_status = 8 and
video_form_status = 5

	and video_form_name =#{video_form_name}

ORDER BY courses_date DESC LIMIT #{nums},#{page}
