mapper.delete:
delete from role_tab
where 1=1 

	 AND role_id = #{role_id}


	 AND role_name = #{role_name}


	 AND role_introduce = #{role_introduce}


	 AND role_status = #{role_status}

mapper.select:
select count(1) from  role_tab
 where 1=1 

	 AND role_id = #{role_id}


	 AND role_name = #{role_name}


	 AND role_introduce = #{role_introduce}


	 AND role_status = #{role_status}

select 
	role_id,
	role_name,
	role_introduce,
	role_status
 from role_tab
 where 1=1 

	 AND role_id = #{role_id}


	 AND role_name = #{role_name}


	 AND role_introduce = #{role_introduce}


	 AND role_status = #{role_status}


	  limit #{page.startNum},#{page.endIndex}
