mapper.delete:
delete from user_tab
where 1=1 

	 AND user_id = #{user_id}


	 AND user_name = #{user_name}


	 AND user_sex = #{user_sex}


	 AND user_phone = #{user_phone}


	 AND user_pwd = #{user_pwd}


	 AND user_status = #{user_status}


	 AND user_date = #{user_date}


	 AND user_email = #{user_email}


	 AND user_photo = #{user_photo}

mapper.select:
select count(1) from  user_tab
 where 1=1 

	 AND user_id = #{user_id}


	 AND user_name = #{user_name}


	 AND user_sex = #{user_sex}


	 AND user_phone = #{user_phone}


	 AND user_pwd = #{user_pwd}


	 AND user_status = #{user_status}


	 AND user_date = #{user_date}


	 AND user_email = #{user_email}


	 AND user_photo = #{user_photo}

select 
	a.user_id,
	a.user_name,
	c.status_type user_sex,
	a.user_phone,
	a.user_pwd,
	b.status_type user_status,
	a.user_date,
	a.user_email,
	a.user_photo
 from user_tab a,status_tab b,status_tab c
 where a.user_status=b.status_id and a.user_sex=c.status_id

	 AND a.user_id = #{user_id}


	 AND a.user_name = #{user_name}


	 AND a.user_sex = #{user_sex}


	 AND a.user_phone = #{user_phone}


	 AND a.user_pwd = #{user_pwd}


	 AND a.user_status = #{user_status}


	 AND a.user_date = #{user_date}


	 AND a.user_email = #{user_email}


	 AND a.user_photo = #{user_photo}


	  limit #{page.startNum},#{page.endIndex}
