mapper.delete:
delete from right_tab
where 1=1 

	 AND right_id = #{right_id}


	 AND right_name = #{right_name}


	 AND right_introduce = #{right_introduce}


	 AND right_status = #{right_status}

mapper.select:
select count(1) from  right_tab
 where 1=1 

	 AND right_id = #{right_id}


	 AND right_name = #{right_name}


	 AND right_introduce = #{right_introduce}


	 AND right_status = #{right_status}

select 
	right_id,
	right_name,
	right_introduce,
	right_status
 from right_tab
 where 1=1 

	 AND right_id = #{right_id}


	 AND right_name = #{right_name}


	 AND right_introduce = #{right_introduce}


	 AND right_status = #{right_status}


	  limit #{page.startNum},#{page.endIndex}
